mapper.select:
SELECT 

FROM shop_product a

WHERE a.id = #{id}

SELECT

FROM shop_product a



		AND a.name LIKE
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')


		AND a.category_id = #{categoryId}


		AND a.market_date = #{marketDate}


		AND a.visits = #{visits}


		AND a.occasion = #{occasion}


		AND a.size = #{size}


		AND a.style = #{style}


		AND a.vamp_texture = #{vampTexture}


		AND a.insole_texture = #{insoleTexture}


		AND a.sales = #{sales}


		AND a.is_new = #{isNew}


		AND a.is_hot = #{isHot}


		AND a.is_alive = #{isAlive}


LIMIT #{startIndex},#{pageSize}

SELECT
COUNT(*)
FROM shop_product a





		AND a.name LIKE
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')


		AND a.category_id = #{categoryId}


		AND a.market_date = #{marketDate}


		AND a.visits = #{visits}


		AND a.occasion = #{occasion}


		AND a.size = #{size}


		AND a.style = #{style}


		AND a.vamp_texture = #{vampTexture}


		AND a.insole_texture = #{insoleTexture}


		AND a.sales = #{sales}


		AND a.is_new = #{isNew}


		AND a.is_hot = #{isHot}


		AND a.is_alive = #{isAlive}

SELECT 

FROM shop_product a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE shop_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
